import React, { useState, useEffect } from 'react';
import API from '../utils/API';
import Wrapper from '../components/Wrapper/Wrapper';
import SearchBar from '../components/SearchBar/SearchBar';
import Results from '../components/Results/Results';

function Search() {
    const [search, setSearch] = useState("");
    const [bookResults, setBookResults] = useState([]);

    // const makeResultsArray = data => {
    //     let newArray = [];
    //     data.map((index) => {
    //         let obj = {
    //             title: index.volumeInfo.title,
    //             author: index.volumeInfo.authors,
    //             image: index.volumeInfo.imageLinks.thumbnail,
    //             description: index.volumeInfo.description,
    //             link: index.saleInfo.buyLink
    //         };
    //         return (
    //             console.log(obj)
    //         )
    //     });

    // };

    const searchBook = event => {
        event.preventDefault();

        API.BookSearch(search)
            .then(res => {
                // console.log("BOOK TITLE IS ", res.data.items[0].volumeInfo.title);
                // console.log("BOOK AUTHOR IS ", res.data.items[0].volumeInfo.authors);
                // console.log("BOOK IMAGE LINK IS ", res.data.items[0].volumeInfo.imageLinks.thumbnail);
                // console.log("BOOK SYNOPSIS IS ", res.data.items[0].volumeInfo.description);
                // console.log("BOOK LINK IS ", res.data.items[0].saleInfo.buyLink)
                setBookResults(res.data.items);
            });
    };

    const handleInputChange = event => {
        setSearch(event.target.value);
    };

    useEffect(() => {
        console.log("OUR NEW BOOK RESULTS ARE ", bookResults);
    },[bookResults]);

    return (
        <Wrapper>
            <SearchBar
                handleInputChange={handleInputChange}
                searchBook={searchBook}    
            />
            <Results />
        </Wrapper>
    );
}

export default Search;
